mapper.select:
select * from data_lake e where normalized is false and skipped is false
#{provider}
order by loaded_at limit 100000

select *
from data_lake
where external_id = #{externalId}
  and provider = #{provider}
order by updated_at desc
limit 1

with datalakes as (
    select * from data_lake
    where external_id = #{externalId}
      and provider = 'pdcMapSrv'
)
select not exists(
        select from datalakes
        where jsonb_object_field_text((data::jsonb)->'properties', 'geohash') = #{geoHash}
    )

select not exists(
        select
        from data_lake
        where external_id = #{externalId}
          and provider = #{provider}
          and updated_at at time zone 'UTC' = #{updatedAt}::timestamptz
    )
