mapper.select:
SELECT * FROM normalized_observations WHERE recombined is false
#{provider}
ORDER BY loaded_at LIMIT 100000

select array_agg(observation_id)::uuid[] from ( SELECT ST_ClusterDBSCAN(ST_Transform(collected_geography::geometry, 54032), 2000, 1) over() as cid, observation_id FROM normalized_observations
#{observationId}
) as clusters group by cid

SELECT *
FROM normalized_observations
WHERE observation_id IN (SELECT observation_id FROM kontur_events WHERE event_id = #{eventId});

SELECT *
FROM normalized_observations
WHERE external_episode_id = #{externalEpisodeId}
  AND loaded_at <= #{loadedAt}
  AND observation_id != #{observationId}
  AND provider = #{provider}
ORDER BY loaded_at DESC
LIMIT 1
